import React, { Component } from 'react';
import { Menu } from './components/Menu';
import { ItemsContainer } from './components/ItemsContainer';
import './App.css';
import dataSpending from './spending.json';
import dataTaxBreak from './taxbreak.json'; 

class App extends Component {
  constructor(){
    super();
    this.state = { 
      active: 0,
      overall:dataSpending.overall,
      single:dataSpending.single
     };
    this.updateIndex = this.updateIndex.bind(this);
  }

  updateIndex(index){
    this.setState({active: index});
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
        <h1>IS IT WORTH IT?</h1>
        <h2>The federal budget includes some very big numbers. This tool breaks down the figures in terms that are easier to digest. Chose the number of taxpayers in your household to see the share for each category and decide for yourself if it is worth it.</h2>
        </header>
        <Menu index={this.state.active} indexHandler={this.updateIndex}/>
        <hr></hr>
        <ItemsContainer index={this.state.active} data={this.state.spendingOverall}/>
      </div>
    );
  }
}

export default App;
