import React, { Component } from 'react';
import { ToggleButton } from './ToggleButton';
import './Toggle.css';

export class Toggle extends Component {
	constructor(){
		super();
		this.state = {
			expanded:false
		}
	}

	handleExpand(){
		this.setState(prevState => ({
			expanded: !prevState.expanded
		  }));
	}

	render() {
		return (
            <div className="Toggle">

            </div>
        )
	}
}
