import React, { Component } from 'react';
import { SubItem } from './SubItem';
import './Item.css';

export class Item extends Component {
	constructor(){
		super();
		this.state = {
			expanded:false
		}
	}

	render() {
		const subs = this.props.subs;
		let subItems = null;
		if (subs.length != 0){
			//console.log(subs);
			subItems = subs.map((label, i) =>
			<SubItem
				key={i}
				index={i}
				left={label.category}
				right={label.amount}
			/>);
		}
		return (
			<div className="Item">
				<div className="col-left">
				<div className="icon icon-expand"></div>
				{this.props.left}
				</div>
				<div className="col-right">
				{this.props.right}
				</div>
				<div class="sub-items">
					{subItems}
				</div>
			</div>
		)
	}
}
