import React, { Component } from 'react';
import { Item } from './Item';
import { Headers } from './Headers';
import './ItemsContainer.css';

export class ItemsContainer extends Component {

	stripComma(str){
		parseInt(str.replace(/,/g, ''), 10)
	}
	render() {
		const items = this.props.data.map((label, i) =>
            <Item
                key={i}
                index={this.props.index}
				left={label.category}
				right={this.props.index === 0 ? ("$" + label.amount):"$" + String(parseInt(label.amount) * this.props.index)}
				subs={label.subs}
            />);
		return (
			 <div className="ItemsContainer">
			 	<Headers left={"Spending Category"} right={"Total Spending (2017)"}/>
				{items}
			 </div>
		)
	}
}
